  //       <select className='dropdown' onChange={this.handleChange}>
  //         {this.state.countryList.map((el) => 
  //       <option key={el}>{el}</option>)}
  //       </select>
  //     </div>
  //   )
  // }


  testing = () => {
    //console.log(this.state.countryList)
    this.state.countryList.map((el) => console.log(el))
    }

  handleChange = (event) => {
    // console.log('changed!')
    // fetch('https://restcountries.eu/rest/v2/all')
    // .then(data => data.json())
    // .then(result => { let { name, currencies, languages, capital, subregion, population, area } = result[136]})

      // console.log(`${name}, ${subregion}, ${area}km2, ${population} people, ${capital}, ${languages[0].name}, ${currencies[0].name}`)
      // this.setState({ countryData: [name, currencies, languages, capital, subregion, population, area] })
      // console.log(this.state.countryData)
    // })
  }

  render() {
    return (
      <div>
        <div className='topContainer'>
          <br/>
          <button onClick={this.testing}>TEST LOG</button><br/>
          <Selection country={this.state.countryList} class='dropdown'/> 
          <br/>
          <h1>{this.state.countrySelection}</h1>
          <img src='https://cdn.countryflags.com/thumbs/russia/flag-round-250.png' alt='flag' height='100px' width='100px'
          />
        </div>
        <br/>
        <div className='bottomContainer'>
          <img src='https://image.flaticon.com/icons/png/128/93/93618.png' alt='' className='smallIcon'/>
          <h3 className='subtitle'>Location: </h3>
          <img src='https://image.flaticon.com/icons/png/128/93/93618.png' alt='' className='smallIcon'/>
          <h3 className='subtitle'>Area: </h3>
          <img src='https://image.flaticon.com/icons/png/128/93/93618.png' alt='' className='smallIcon'/>
          <h3 className='subtitle'>Population: </h3>
          <img src='https://image.flaticon.com/icons/png/128/93/93618.png' alt='' className='smallIcon'/>
          <h3 className='subtitle'>Capital: </h3>
          <img src='https://image.flaticon.com/icons/png/128/93/93618.png' alt='' className='smallIcon'/>
          <h3 className='subtitle'>Languages: </h3>
          <img src='https://image.flaticon.com/icons/png/128/93/93618.png' alt='' className='smallIcon'/>
          <h3 className='subtitle'>Currencies: </h3>
        </div>
      </div>
    )
  }

}

export default App;


